import React, { useState } from "react";
import "./InvestmentForm.css";

const InvestmentForm = (props) => {
  const [cSaving, setcSaving] = useState("");
  const [ySaving, setyysaving] = useState("");
  const [investmentDuration, setInvestmentDuration] = useState("");
  const [expectedInterest, setexpectedInterest] = useState("");

  const currentSavingHandler = (event) => {
    setcSaving(event.target.value);
  };

  const yearlyContributionHandler = (event) => {
    setyysaving(event.target.value);
  };

  const expectedInterestHandler = (event) => {
    setexpectedInterest(event.target.value);
  };

  const investmentDurationHandler = (event) => {
    setInvestmentDuration(event.target.value);
  };

  const submitFromHandler = (event) => {
    event.preventDefault();
    props.onSubmitHandler({
      cSaving,
      ySaving,
      investmentDuration,
      expectedInterest,
    });
  };
  return (
    <div>
      <form className="form" onSubmit={submitFromHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input
              type="number"
              id="current-savings"
              onChange={currentSavingHandler}
            />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input
              type="number"
              id="yearly-contribution"
              onChange={yearlyContributionHandler}
            />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input
              type="number"
              id="expected-return"
              onChange={expectedInterestHandler}
            />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input
              type="number"
              id="duration"
              onChange={investmentDurationHandler}
            />
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    </div>
  );
};

export default InvestmentForm;
